extension.valueString:
define "SDE Sex":
  case
      when Patient.gender = 'male' then Code { code: 'M', system: 'http://hl7.org/fhir/v3/AdministrativeGender', display: 'Male' }
      when Patient.gender = 'female' then Code { code: 'F', system: 'http://hl7.org/fhir/v3/AdministrativeGender', display: 'Female' }
      else null
    end

//    sort by start of effective 
        //)
define "Initial Population":
  exists("Index Depression Assessment List" IndexAssessment
                where AgeInYearsAt(start of Global."Normalize Interval"(IndexAssessment.effective)) >= 12
                 and "Index Depression Assessment List" is not null)

define "SDE Payer":
  [Coverage: type in "Payer"] Payer
        return {
          code: Payer.type,
          period: Payer.period
        }
